import './bestStyle.scss'
import React, {useState} from "react";


const black = 'black';
const  grey= 'grey';

class Exercise4 extends React.Component{

    constructor(props){
        super(props);
        this.state = { color: black };
        this.changeColor = this.changeColor.bind(this);
      }
      changeColor() {
        const newColor = this.state.color == black ? grey : black;
        this.setState({ color: newColor });
      }
      render(){
        return(
    <div style={{backgroundColor: 'white' , margin:'20px', padding:'20px'}}>
        <h1><em><u>Exercise 4:</u></em></h1>
          <div style={{background: this.state.color}}>
          <h1>theme</h1>
          <button onClick={this.changeColor}>Click to change light theme</button>
          </div>
    </div>
        )
      }
    }

export default Exercise4;
