import React, {useState} from 'react'
// import Toast from 'react-native-simple-toast';
import { Button} from 'reactstrap';
const avaliableTeamName = ['Einstein','Netwon','Tesla'];
const avaliableName = ["Venkat",
"Kevin",
"Kinjal",
"Eric",
"Ronish",
"Prakash",
"Lalit"]


function Lottery(){
    const [team,setTeam] =useState([]);
    const[teamName,setTeamName] = useState('');

   const handleLottery = () =>{
       let candName = randomTeam(avaliableName)
       let teamCount=3
        var teamForm  = [];
        while(teamCount>0){
            teamForm.push(candName.splice(0,Math.floor(candName.length/teamCount)))
            teamCount--;
        }
      setTeam(teamForm);
      console.log(teamForm);
    }


    function randomTeam(arr){
        for(let i = arr.length-1;i>0;i--){
            let j = Math.floor(Math.random()*i);
            let temp = arr[i];
            arr[i] = arr[j];
            arr[j] = temp;
        }
        return arr;
    }


    return(
            <div style={{backgroundColor: 'white' , margin:'20px', padding:'20px'}}>
                <h1><em><u>Lottery :</u></em></h1>
                <div className="lottery-container"></div>
                <button onClick={handleLottery(randomTeam(avaliableName))} color="primary">Start Lottery</button>{' '}
     <div>
    <h2>{avaliableTeamName[0]}</h2>
    <p>
        {team.slice(0,1).join(" , ")}
    </p>
    <h2>{avaliableTeamName[1]}</h2>
    <p>{team.slice(1,2).join(" , ")}</p>
    <h2>{avaliableTeamName[2]}</h2>
    <p>{team.slice(2).join(" , ")}</p>
    </div>


    </div>
    )
}

export default Lottery;
