import React from "react";
import $ from 'jquery';

const black = 'black';
const  yellow= 'yellow';

class Exercise4 extends React.Component{

    constructor(props){
        super(props);
        this.state = { 
          color: black,
          value: "Light Theme"
        };
        this.changeColor = this.changeColor.bind(this);
      }

      changeColor(text) {
        const newColor = this.state.color == black ? yellow : black;
        const isToggleOn = this.state.isToggleOn == "light Theme" ? 'Dark Theme' : 'Light Theme'
        this.setState({ color: newColor});
      }


      render(){
        return(
    <div style={{backgroundColor: 'white' , margin:'20px', padding:'20px'}}>
        <h1><em><u>Exercise 4:</u></em></h1>
          <div style={{background: this.state.color, height:'500px',boxSizing:'border-box'}}>
          <h1 style={{color:'grey', margin:'10px'}} value="Light Theme"></h1>
          <button class= "btn " value="Light Theme" onClick={this.changeColor} style={{justifyItems:'baseline',marginTop:'432px'}}>
            "Light Theme"
          </button>
          </div>
    </div>
        )
      }
    }

export default Exercise4;
